import React from 'react';
import styled from 'styled-components';
import { 
    page_transition_content_progress, 
    keyframe_fadein,
    keyframe_fadeout
 } from '../../src/KeyFrames';
import { breakPoints, colorOptions, base } from '../../src/theme';

const PageTransition = styled.div`
    position: fixed;
    top: 0px;
    left: 0px;
    right: 0px;
    bottom: 0px;
    z-index: 9999;
    overflow: hidden;
    transform: scaleX(0);
    transition: transform cubic-bezier(0.19, 1, 0.22, 1) 1s 1s;
    transform-origin: center right; 
    width: 100%;
    height: 100%;
    &.active {
        transform: scaleX(1);
        transform-origin: center left;
        transition-delay: 1s;
        .pg-t-swipe {
            transform: translateX(-100%);
            transition: none;
            transition-delay: 0s;
        }
    }
    .relative {
        position: relative;
    }
`;

const PageTPanel = styled.div`
    position: absolute;
    top: 0px;
    left: 0px;
    width: 100%;
    height: 100%;
    background: #011118;
    z-index: -1;
`;

const PageTSwipe = styled.div`
    position: absolute;
    background: #fff;
    width: 200%;
    height: 100%;
    top: 0px;
    left: 0px;
    transform: translateX(100%);
    transition: transform cubic-bezier(0.19, 1, 0.22, 1) 3.2s;
    transition-delay: 0.3s;

    &.pg-delay-2 {
        background: ${colorOptions.red.PRIMARY_COLOR_BOLD};
        transition-delay: 0.6s;
    }
`;

const PageTContent = styled.div`
    display: flex;
    justify-content: center;
    flex-direction: column;
    align-items: center;
    height: 100vh;
    animation: ${keyframe_fadeout} 0.5s ease-out;
    animation-delay: 1500;
    opacity: 0;
    > * {
        max-width: ${breakPoints.lg}px;
        width: 90%;
    }
    &.active {
        animation: ${keyframe_fadein} 1s ease-in;
        opacity:1;
    }
    @media screen and (max-width: ${breakPoints.lg}px) {
        width: 100%;
    }
`;

const TransitionProgress = styled.div`
    background: rgba(255, 255, 255, 0.1);
    height: 1px;
    width: 100%;
    margin-top: 48px;
    overflow: hidden;
    position: relative;

    &::before {
        content: '';
        width: 120px;
        height: 1px;
        display: block;
        position: absolute;
        background: white;
        animation-duration: 2s;
        animation-timing-function: ease;
        animation-iteration-count: infinite;
        animation-name: ${page_transition_content_progress};
    }
`;

const StyledH2 = styled.h2`
    color:#fff;
    font-weight: bold;
`;

const StyledH4 = styled.h4`
    color:#fff;
    font-weight: bold;
    opacity: 0.5;
    @media screen and (max-width: ${breakPoints.md}px) {
        font-size: ${base.FONT_SIZE_MEDIUM}px;
    }
`;

const Transition = (props) => {

    let classnames = props.triggerTransition ? 'active':'';

    return (
        <PageTransition className={classnames}>
            <PageTPanel/>
            <PageTSwipe className="pg-t-swipe"/>
            <PageTSwipe className="pg-t-swipe pg-delay-2"/>
            <PageTContent className={classnames}>
                <div>
                    <StyledH2>Hien Luong</StyledH2>
                    <StyledH4>Software Engineer at Greater Seattle Area</StyledH4>
                    <TransitionProgress/>
                </div>
            </PageTContent>
        </PageTransition>
    )
}

export default Transition;
