import React from 'react';
import styled from 'styled-components';

const Wrapper = styled.span`
    display: inline-block;

`;

const HoverEffectText = ({children }) => {

    return (
        <Wrapper>
            {children}
        </Wrapper>
    )
}

HoverEffectText.defaultProps = {
    element: 'div'
}

export default HoverEffectText;
