import React from 'react';
import AppBar from '@material-ui/core/AppBar';
import Grid from '@material-ui/core/Grid';
import Toolbar from '@material-ui/core/Toolbar';
import IconButton from '@material-ui/core/IconButton';
import Icon from '@material-ui/core/Icon';
import styled from "styled-components";
import { SCLink } from '../../src/CustomEl';
import ClipboardCaption from '../../components/ClipboardCaption';
import Link from 'next/link';

const StyledAppBar = styled(({ color, ...otherProps }) => (
  <AppBar {...otherProps} />
))`
  && {
    background-color: #fff;
    img {
        color: #000;
        width: 40px;
    }
  }
`;

const FlexGrowDiv = styled.nav`
  flex-grow: 1;
`;

const BigMenu = styled.div`
  position: fixed;
  width: 100%;
  height: 0%;
  background-color: #021117;
  z-index:1;
  overflow: hidden;
  transition: all 0.3s ease-in-out;
  &.active {
    height: 100%;
    padding-top: 10em;
    ul {
      li {
        opacity: 1;
        transform: translateY(0px);
      }
    }
  }
`;

const StyledToolbar = styled((props) => (
  <Toolbar {...props} />
))`
  && {        
      z-index: 2;
  }
`;

const List = styled.ul`
  color: #fff;
  list-style: none;
  padding: 0;
  li {
    transition: transform cubic-bezier(0.19, 1, 0.22, 1) 1s 0.6s, opacity cubic-bezier(0.19, 1, 0.22, 1) 1s 0.6s, color linear 0.2s;
    margin-bottom: 24px;
    min-height: 36px;
    transition: transform cubic-bezier(0.19, 1, 0.22, 1) 1s 0s, opacity cubic-bezier(0.19, 1, 0.22, 1) 1s 0s;
    transform: translateY(32px);
    opacity: 0;

    small {
      font-size: 16px;
      opacity: 0.75;
      font-weight: bold;
      position: relative;
      &:before {
        content: '';
        position: absolute;
        bottom: -5px;
        height: 3px;
        width: 50%;
        background: #e31e0c;
      }
    }
    span, a {
      cursor: pointer;
      display: block;
      font-size: 24px;
      font-weight: 700;
      text-decoration: none;
      color: #fff;
      display: inline-block;
    }
    h3 {
      margin-bottom: 8px;
    }
    h5 {
      opacity: 0.25;
      margin-bottom: 40px;
      line-height: 24px;
    }
  }
`;

class Header extends React.Component {

  state = {
    iconColor: "action",
    menuOpen: false 
  }

  toggleModal = () => {
    let {
      menuOpen
    } = this.state;

    this.setState({
      menuOpen: !menuOpen,
      iconColor: "inherit",
    })
  }

  render () {

    let {
      iconColor,
      iconName,
      menuOpen
    } = this.state;

    return (
        <StyledAppBar position="static" elevation={0}>
            <StyledToolbar>
              <Link href="/"><img src="../static/images/logo-v3.png"/></Link>
              <FlexGrowDiv/>
              <IconButton edge="start" color="inherit" aria-label={iconName} onClick={this.toggleModal}>
                {menuOpen ? 
                  <Icon fontSize="large" color="inherit">close</Icon> :
                  <Icon fontSize="large" color="action">menu</Icon>
                }
              </IconButton>
              {/* <nav>
                <SCLink href="#intro" onClick={this.scrollToEl}>
                  <span >Intro</span>
                </SCLink>
                <SCLink href="#about" >
                  <span onClick={this.scrollToEl}>About</span>
                </SCLink>
                <SCLink href="#work" >
                  <span onClick={this.scrollToEl}>Work</span>
                </SCLink>
                <SCLink href="#skills" >
                  <span onClick={this.scrollToEl}>Skills</span>
                </SCLink>
                <SCLink href="#contact" >
                  <span onClick={this.scrollToEl}>Contact</span>
                </SCLink>
              </nav> */}
            </StyledToolbar>
            <BigMenu className={menuOpen ? "active":""}>
              <Grid
                container
                spacing={0}
                direction="row"
                justify="center"
                alignItems="flex-start"
                style={{minHeight: '100vh'}}
              >
                <Grid item md={6}>
                  <Grid item container direction="row" spacing={0}>
                    <Grid item md={5}>
                      <List>
                        <li><small>Explore work</small></li>
                        <li>
                          <a href="/work">
                            <h3>Project Title</h3>
                            <h5>The tool that does it all.</h5>
                          </a>
                        </li>
                        <li>
                          <a href="/work">
                            <h3>Project Title</h3>
                            <h5>The tool that does it all.</h5>
                          </a>
                        </li>
                      </List>
                    </Grid>
                    <Grid item md={6}>
                      <List>
                        <li></li>
                        <li>
                          <a href="/work">D&amp;J Corporation Kindergarten</a>
                        </li>
                        <li>
                          <a href="/work">Educate Whimsy Games</a>
                        </li>
                        <li>
                          <a href="/work">A.S+C.E.N.D</a>
                        </li>
                      </List>
                    </Grid>
                  </Grid>
                </Grid>
                <Grid item md={3}>
                  <List>
                    <li><small>Learn more</small></li>
                    <li><Link href="/about"><a>About</a></Link></li>
                    <li><a href="https://medium.com/@phuhien" target="_blank">Blog</a></li>
                    <li>
                      <ClipboardCaption
                        copytext="luongphuhien@gmail.com"
                        buttontext="Get in Touch"
                        message="My email has been copied to your clipboard! ðŸŽ‰"
                        timeout={2000}
                      />
                    </li>
                  </List>
                </Grid>
              </Grid>
            </BigMenu>
        </StyledAppBar>
    )
  }
}

export default Header;
