import React from 'react';
import App, { Container } from 'next/app';
import Router from 'next/router';
import Head from 'next/head';
import CssBaseline from '@material-ui/core/CssBaseline';
import Transition from '../components/transition';
import Header from "../components/layout/header";
import Footer from "../components/layout/footer.copy";
import { GlobalStyle } from "../src/global-styles";

class MyApp extends App {

    state = {
        triggerTransition: true,
        isNavOpened: true,
        navPosition: "fixed",
        overflowClassname: "nav-opened",
        menuColor: "action",
        transitionTimeout: 2000,
        pageTitle: "Hien Luong"
    }

    componentDidMount() {
        const jssStyles = document.querySelector('#jss-server-side');
        if (jssStyles) {
            jssStyles.parentNode.removeChild(jssStyles)
        }

        let {
            triggerTransition,
            overflowClassname
        } = this.state;

        setTimeout(() => {
            this.setState({
                triggerTransition: !triggerTransition
            }, () => {
                document.body.classList.remove(overflowClassname)
            });
        }, this.state.transitionTimeout)

        Router
    }

    setMenuColor = (color) => {
        this.setState({
            menuColor: color
        })
    }

    setPageTitle = (pageTitle) => {
        this.setState({
            pageTitle: pageTitle
        })
    }

    toggleNav = () => {
        let {
          isNavOpened,
          overflowClassname
        } = this.state;

        (!isNavOpened) ?
            document.body.classList.add(overflowClassname) :
            document.body.classList.remove(overflowClassname)

        this.setState({
            isNavOpened: !isNavOpened,
        })
    }

    render() {
        let {
            Component,
            pageProps
        } = this.props;

        let {
            isNavOpened,
            navPosition,
            triggerTransition,
            overflowClassname,
            menuColor,
            pageTitle
        } = this.state;

        return (
            <Container>
                <Head>
                    <title>
                        {pageTitle} | Portfolio
                    </title>
                </Head>
                <>
                    <GlobalStyle/>
                    <CssBaseline/>

                    <Transition triggerTransition={triggerTransition} overflowClassname={overflowClassname}/>
                    <Header 
                        toggleNav={this.toggleNav} 
                        isNavOpened={isNavOpened} 
                        navPosition={navPosition}
                        menuColor={menuColor}
                    />
                    <Component 
                        {...pageProps} 
                        setMenuColor={this.setMenuColor}
                        setPageTitle={this.setPageTitle}
                    />
                    <Footer/>
                </>
            </Container>
        )
    }
}

export default MyApp;
