import React from 'react';
import styled, {css} from 'styled-components';

const Wrapper = styled.span`
    display: inline-block;

    && css`

    `
`;

const HoverEffectText = ({children, textColor, backgroundColor }) => {
    return (
        <Wrapper>
            {children}
        </Wrapper>
    )
}

export default HoverEffectText;
