import React from 'react';
import Router from 'next/router';

const Link = ({href,query, children}) => {

    const goTo = () => {

        let router = {
            pathname: href
        }

        if (query) router['query'] = query;

        Router.push(router);
    }

    return (
        <span href={href} onClick={goTo}>
            {children}
        </span>
    )
}

export default Link
