import React from 'react';
import styled, {css} from 'styled-components';

const Wrapper = styled.span`
    display: inline-block;

    && css`

    `
`;

const HoverEffectText = (props) => {
    return (
        <Wrapper {...props}>
            {props.children}
        </Wrapper>
    )
}

export default HoverEffectText;
