import React, {useState, useEffect} from 'react';
import Axios from 'axios';
import { API } from './../../API';
import styled from 'styled-components';
import { breakPoints, colorOptions } from '../../src/theme';
import WIcon from './WIcon';

const ipLookup = async () => {
    try {
        const res = await Axios.get(API.IPLOOKUP_API_PATH);
        const data = res.data;

        return data;
    } catch (error) {
        console.error(error);
    }
}

const Wrapper = styled.div`
    position: relative;
    /* border: 5px solid ${colorOptions.orange.PRIMARY_COLOR}; */
    border-radius: 30px;
    box-shadow: 15px 20px 85px #d7c4bd;
    overflow: hidden;
    background: linear-gradient(141deg,#f9ebe2 30%, #e7c3bb 75%);
`;

const Card = styled.div`
    z-index: 1;
    background: #fff;
    position: relative;
    padding: 15px;
    h5 {
        top: -15px;
        left: 15px;
        position: absolute;
    }
    h2 {
        text-align: left;
    }
`;

const wIcons = {
    Sunny: ['wi-day-sunny', '#FDD835'],
    Rain: ['wi-rain', '#2196F3'],
    Cloudy: ['wi-cloudy', '#CFD8DC'],
    Mist: ['wi-fog', '#90A4AE'],
    Thunderstorm: ['wi-thunderstorm', '#37474F'],
    Snow: ['wi-snow', '#B2EBF2'],
    Drizzle: ['wi-sprinkle', '#2196F3']
};

const WeatherText = styled.div`
    position: absolute;
    right: -2em;
    top: -2em;
    text-align: right;
    img {
        width: 80px;
    }
    p {
        margin: 0;
    }
`;

const Wave = styled.div`
    height: 31px;
    background-image: url(data:image/png;base64,);
    background-position: -80px 0px;
    background-size: 100%;
    width: 100%;
    position: relative;
`;

const getCurrentWeather = (query, units) => Axios.get(`${API.WEATHER_API_PATH}?appid=${API.WEATHER_API_ACCESS_KEY}&q=${query}&units=${units}`);

const Weather = (props) => {

    const [longitude, setLongitude] = useState(-122.33);
    const [latitude, setLatitude]   = useState(47.6);
    const [city, setCity]           = useState("Seattle");
    const [country, setCountry]     = useState("United State");
    const [weather, setWeather]     = useState({});

    useEffect(() => {
        ipLookup()
        .then(success => {

            let data = !!success ? 
            {
                ...success,
                longitude: success.lon,
                latitude: success.lat
            } :
            {
                latitude: latitude,
                longitude: longitude,
                city: city,
                country: country
            };

            setLatitude(data.latitude);
            setLongitude(data.longitude);
            setCity(data.city);
            setCountry(data.country);

            getCurrentWeather(`${city},${country}`, 'imperial')
            .then(success => {
                let {data} = success;
                console.log(data)
                setWeather({
                    ...data.main,
                    name: data.name,
                    weatherMain: data.weather[0].main,
                    weatherText: data.weather[0].description,
                    icon: `${API.WEATHER_API_ICON_PATH}/${data.weather[0].icon}@2x.png`,

                })
            })
            .catch(error => {
                console.error("Current Weather Error: ", error);
            })
        })
    }, [])

    return (
        <Wrapper>
            <WeatherText>
                <WIcon type="snow" text={weather.weatherText}></WIcon>
            </WeatherText>
            {props.children}
            <Wave/>
            <Card>
                <h5>{weather.name}</h5>
                <h2>
                    {Math.round(weather.temp)}°
                    <small>F</small>
                </h2>

            </Card>
        </Wrapper>
    )
}

export default Weather;

Weather.defaultProps = {

}
