import React from 'react';
import styled, {css} from 'styled-components';
import { breakPoints, colorOptions } from '../../../src/theme';
import Weather from '../../Weather';

const ImageContainer = styled.div`
    max-height: 400px;
    max-width: 400px;
    position: relative;
    text-align: center;
    margin: auto;

    &.active {
        transform: translateY(30px);
        opacity: 1;
    }
`;

const AvatarImage = styled.picture`
    width: 100%;
    position: relative;
    display: block;
    bottom: -30px;
    /* 
    border-radius: 50%;
    border: 15px solid ${colorOptions.orange.PRIMARY_COLOR}; 
    background-size: contain;
    ${props => css`
        background: ${colorOptions.orange.PRIMARY_COLOR_LIGHT} url(${props.background}) no-repeat center left;
    `}*/

    img {
        position: relative;
        width: 100%;
        width: inherit;
        bottom: -6px;
    }

    @media (max-width: ${breakPoints.md}px) {
        width: 80%;
    }
`;



const Image = () => {

    let background = "static/images/sun_w_1.jpg";

    return (
        <ImageContainer className="active">
            <Weather>
                <AvatarImage>
                    <img src="static/images/avatar.png" alt="avatar"></img>
                </AvatarImage>
            </Weather>

        </ImageContainer>
    )
}

export default Image;
